/*
 * Title: Largest product in a series
 *
 * Problem #: 8
 * 
 * Description: Find the greatest product of five consecutive digits in 
 * the 1000-digit number.
 * 73167176531330624919225119674426574742355349194934
 * 96983520312774506326239578318016984801869478851843
 * 85861560789112949495459501737958331952853208805511
 * 12540698747158523863050715693290963295227443043557
 * 66896648950445244523161731856403098711121722383113
 * 62229893423380308135336276614282806444486645238749
 * 30358907296290491560440772390713810515859307960866
 * 70172427121883998797908792274921901699720888093776
 * 65727333001053367881220235421809751254540594752243
 * 52584907711670556013604839586446706324415722155397
 * 53697817977846174064955149290862569321978468622482
 * 83972241375657056057490261407972968652414535100474
 * 82166370484403199890008895243450658541227588666881
 * 16427171479924442928230863465674813919123162824586
 * 17866458359124566529476545682848912883142607690042
 * 24219022671055626321111109370544217506941658960408
 * 07198403850962455444362981230987879927244284909188
 * 84580156166097919133875499200524063689912560717606
 * 05886116467109405077541002256983155200055935729725
 * 71636269561882670428252483600823257530420752963450
 *
 * Author: epicoxymoron
 * Language: C99
 */

#include "euler8.h"

int product_of(const char* str, size_t start_pos, size_t product_len) {
	size_t i;
	int var;

	int num = 1;
	for (i = start_pos; i < start_pos + product_len; i++) {
		/* numbers in ASCII range from 48 to 57 */
		assert(str[i] >= 48 && str[i] <= 57);
		var = str[i] - 48;
		num *= var;
	}
	return num;
}

int max_product_pos(const char* str, size_t str_len, size_t product_len, int *val) {
	size_t max_product_pos = 0;
	int max_product_val = -1;
	size_t i;
	int cur_product_val;

	for (i = 0; i < str_len - product_len + 1; i++) {
		cur_product_val = product_of(str, i, product_len);
		if (cur_product_val > max_product_val) {
			max_product_val = cur_product_val;
			max_product_pos = i;
		}
	}

	*val = max_product_val;

	return max_product_pos;
}

void print_results(const char *str, size_t product_len, size_t start_pos, int val, bool verbose) {
	if (verbose) {
		char str_max[product_len + 1];
		strncpy(str_max, &str[start_pos], product_len);
		str_max[product_len] = '\0';

		printf("In the string:\n\t%s\n\n", str);
		printf("The substring of size %lu with the largest by-digit ", product_len);
		printf("product is \"%s\", starting at position %lu.\n", str_max, start_pos);
		printf("Product: %d\n", val);
	}
	else {
		printf("%d\n", val);
	}
}

int main(int argc, char** argv) {
	char c;
	size_t pos;
	int val;
	bool verbose = false;

	const char str[] = "";


	while (1) {
		static struct option long_options[] = {
			/* These options set a flag. */
			{"verbose", no_argument, 0, 'v'},
			{0, 0, 0, 0}
		};
		int option_index = 0;
		c = getopt_long(argc, argv, "v", long_options, &option_index);

		if (c == -1) {
			break;
		}

		switch (c) {
			case 'v':
				verbose = true;
				break;
			default:
				break;
		}
	}

	pos = max_product_pos(str, 1000, 5, &val);
	print_results(str, 5, pos, val, verbose);
	return 0;
}
